import { uiActions } from "./ui-slice";
import axios from 'axios';

export const fetchCartData = () => {
    return (dispatch) => {
        axios.get("https://react-hooks-c25a3-default-rtdb.europe-west1.firebasedatabase.app/cart.json")
            .then(res => {
                return res.data;
            }).catch(error => {
                dispatch(uiActions.showNotification({
                    status: 'error',
                    title: 'Error!',
                    message: 'Fetching cart data failed!'
                  }))
            })
    }
}

export const sendCartData = (cart) => {
    return (dispatch) => {
        dispatch(
            uiActions.showNotification({
                status: 'pending',
                title: 'Sending...',
                message: 'Sending cart data!'
            })
        );

        axios.put("https://react-hooks-c25a3-default-rtdb.europe-west1.firebasedatabase.app/cart.json", cart)
        .then(res => {
          dispatch(uiActions.showNotification({
            status: 'success',
            title: 'Success!',
            message: 'Sending cart data successfully!'
          }))
        }).catch(error => {
          dispatch(uiActions.showNotification({
            status: 'error',
            title: 'Error!',
            message: 'Sending cart data failed!'
          }))
        });
    }
};
